import React, { useState, createContext, useContext } from "react"

const createContextHook = (initialContext) => {
	const context = createContext({ contextHook: [initialContext, () => { }] });
	const useCtx = () => useContext(context).contextHook;
	const CtxProvider = (props) => {
			const [state, setState] = useState(initialContext);
			//useEffect(() => { setState(s => null) }, []);
			return (<context.Provider value={{ contextHook: [state, setState] }}>
				{props.children}
			</context.Provider>)
		}
	return [useCtx, CtxProvider, context];
}

export default createContextHook;