import React, { useState, createContext } from "react"

const createContextHook = (initialState) => {
	const context = createContext({ contextHook: [initialState, ()=>{}] });
	const Provider = (props) => {
			const [state, setState] = useState(initialState);
			//useEffect(() => { setState(s => null) }, []);
			return (<context.Provider value={{ contextHook: [state, setState] }}>
				{props.children}
			</context.Provider>)
		}
	return [context, Provider];
}

export default createContextHook;