import React from "react";
import ColorPickerContext from "./Context";

const display = props => (
	<ColorPickerContext.Consumer>
		{context => (
			<div
				className={[
					context.theme && context.theme.display,
					props.className
				]
					.join(" ")
					.trim()}
				style={{
					...context.checker(context.text),
					...props.style
				}}
				onClick={props.onClick}
			>{props.children}</div>
		)}
	</ColorPickerContext.Consumer>
);

export default display;
