import React from "react";
import ColorPickerContext from "../Context";
import { hsl2string } from "../utils/converters";

const luminosity = props => (
	<ColorPickerContext.Consumer>
		{context => {
			const HSL = context.hsl;
			const midL = { h: HSL.h, s: HSL.s, l: 50 };
			return (
				<input
					className={[
						context.theme && context.theme.luminosity,
						context.theme && context.theme.slider,
						props.className
					]
						.join(" ")
						.trim()}
					style={{
						WebkitAppearance: "none",
						background: `linear-gradient(90deg, black,${hsl2string(midL)},white)`,
						...props.style
					}}
					type="range"
					onChange={e => {
						let HSL = context.hsl;
						HSL.l = e.target.value;
						context.updateColorHSL(HSL);
					}}
					min="0"
					max="100"
					value={context.hsl.l}
					step="1"
				/>
			);
		}}
	</ColorPickerContext.Consumer>
);

export default luminosity;
