import React from "react";
import ColorPickerContext from "../Context";
import { hsl2string } from "../utils/converters";

const saturation = props => (
	<ColorPickerContext.Consumer>
		{context => {
			const HSL = context.hsl;
			const lowS = { h: HSL.h, s: 0, l: HSL.l };
			const highS = { h: HSL.h, s: 100, l: HSL.l };
			return (
				<input
					className={[
						context.theme && context.theme.saturation,
						context.theme && context.theme.slider,
						props.className
					]
						.join(" ")
						.trim()}
					style={{
						WebkitAppearance: "none",
						background: `linear-gradient(90deg, ${hsl2string(lowS)},${hsl2string(highS)}`,
						...props.style
					}}
					type="range"
					onChange={e => {
						let HSL = context.hsl;
						HSL.s = e.target.value;
						context.updateColorHSL(HSL);
					}}
					min="0"
					max="100"
					value={context.hsl.s}
					step="1"
				/>
			);
		}}
	</ColorPickerContext.Consumer>
);

export default saturation;
