import React from "react";
import ColorPickerContext from "../Context";
import { rgb2string } from "../utils/converters";

const blue = props => (
	<ColorPickerContext.Consumer>
		{context => {
			const RGB = context.rgb;
			const lowB = { r: RGB.r, g: RGB.g, b: 0 };
			const highB = { r: RGB.r, g: RGB.g, b: 255 };
			return (
				<input
					className={[
						context.theme && context.theme.blue,
						context.theme && context.theme.slider,
						props.className
					]
						.join(" ")
						.trim()}
					style={{
						WebkitAppearance: "none",
						background: `linear-gradient(90deg, ${rgb2string(lowB)},${rgb2string(highB)}`,
						...props.style
					}}
					type="range"
					onChange={e => {
						let RGB = context.rgb;
						RGB.b = e.target.value;
						context.updateColorRGB(RGB);
					}}
					min="0"
					max="255"
					value={context.rgb.b}
					step="1"
				/>
			);
		}}
	</ColorPickerContext.Consumer>
);

export default blue;
