import React from "react";
import ColorPickerContext from "../Context";
import { rgb2string } from "../utils/converters";

const green = props => (
	<ColorPickerContext.Consumer>
		{context => {
			const RGB = context.rgb;
			const lowG = { r: RGB.r, g: 0, b: RGB.b };
			const highG = { r: RGB.r, g: 255, b: RGB.b };
			return (
				<input
					className={[
						context.theme && context.theme.green,
						context.theme && context.theme.slider,
						props.className
					]
						.join(" ")
						.trim()}
					style={{
						WebkitAppearance: "none",
						background: `linear-gradient(90deg, ${rgb2string(lowG)},${rgb2string(highG)}`,
						...props.style
					}}
					type="range"
					onChange={e => {
						let RGB = context.rgb;
						RGB.g = e.target.value;
						context.updateColorRGB(RGB);
					}}
					min="0"
					max="255"
					value={context.rgb.g}
					step="1"
				/>
			);
		}}
	</ColorPickerContext.Consumer>
);

export default green;
