import React from "react";
import ColorPickerContext from "../Context";
import { rgb2string } from "../utils/converters";

const red = props => (
	<ColorPickerContext.Consumer>
		{context => {
			const RGB = context.rgb;
			const lowR = { r: 0, g: RGB.g, b: RGB.b };
			const highR = { r: 255, g: RGB.g, b: RGB.b };
			return (
				<input
					className={[
						context.theme && context.theme.red,
						context.theme && context.theme.slider,
						props.className
					]
						.join(" ")
						.trim()}
					style={{
						WebkitAppearance: "none",
						background: `linear-gradient(90deg, ${rgb2string(lowR)},${rgb2string(highR)}`,
						...props.style
					}}
					type="range"
					onChange={e => {
						let RGB = context.rgb;
						RGB.r = e.target.value;
						context.updateColorRGB(RGB);
					}}
					min="0"
					max="255"
					value={context.rgb.r}
					step="1"
				/>
			);
		}}
	</ColorPickerContext.Consumer>
);

export default red;
