import React from "react";
import ColorPickerContext from "./Context";
import { string2color } from "./utils/converters";

const swatch = props => (
	<ColorPickerContext.Consumer>{
		context=>
			<div
				className={[
					context.theme && context.theme.swatch,
					props.className
				]
					.join(" ")
					.trim()}
				style={{
					...context.checker(props.color),
					...props.style
				}}
				onClick={() => { context.updateColor(string2color(props.color)) }}
			>{props.children}</div>
		}</ColorPickerContext.Consumer>
)

export default swatch;