import DB from "DB";

const query = (query, props = [], process) => {
	DB.getCon().query(query, props, (err, rows, fields) => {
		if (err) {
			console.log("[mysql error]", err);
			//res.status(500).json({error: "mysql error"});
		} else {
			process && process(rows, fields);
		}
	});
	DB.end();
}

export { query };